//	#Navigation
//	==================================================

	$(document).ready(function() {
		var	navLinks = $('#nav .no-touch').html();

		$('#nav .touch #touch-nav').append(navLinks);

		$('#nav-toggle').click(function() {
			$(this).next('#touch-nav').stop().slideToggle();
		});
	});


//	#Featured Icon
//	==================================================

	$(document).ready(function(){
		$('.featured-icon').hover(function() {
			$(this).next('.featured-text').stop().fadeIn();
		}, function() {
			$(this).next('.featured-text').stop().fadeOut();
		});
	});

//	#Responsive Videos
//	==================================================

	$(document).ready(function(){
		$('.article-body').fitVids();
	});


//	#Post Comments
//	==================================================

	$(document).ready(function() {
		var disable_comments		=	false, // Possible values: true, false
			comment_plugin			=	'disqus', // Possible values: 'disqus', 'facebook'
			disqus_shortname		=	'CloudEightDesigns';

		if (disable_comments) {
			$('#comments').remove();
		} else {
			if (comment_plugin == 'disqus') {
				$('.fb-comments').remove();

				if (!disqus_shortname || disqus_shortname == '' || disqus_shortname.length < 1) {
					$('#comment-plugin').remove();
				} else {
					(function() {
						var dsq = document.createElement('script'); dsq.type = 'text/javascript'; dsq.async = true;
						dsq.src = '//' + disqus_shortname + '.disqus.com/embed.js';
						(document.getElementsByTagName('head')[0] || document.getElementsByTagName('body')[0]).appendChild(dsq);
					})();
				}
			} else if (comment_plugin == 'facebook') {
				$('#disqus_thread').remove();
			} else {
				$('#comment-plugin').remove();
			}
		}
	});


//	#Scroll To Top
//	==================================================

	$(document).ready(function() {
		$('.scroll-to-top').click(function () {
			$('html, body').animate({ scrollTop: 0 }, 1600, 'easeInOutQuart');
			return false;
		});
	});